mapper.select:
SELECT

FROM  a

WHERE
1 =1



	and a.id = #{id}





	and a.href = #{href}

SELECT
a.sort
FROM  a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	and grade = #{grade}


	and parent_id = #{parentID}


ORDER BY a.sort desc limit 1

select distinct menu.*, ur.user_id from menu join t_sys_role_menu rm on menu.id = rm.menu_id join t_sys_role_info r on rm.role_id = r.id and r.del_flag = 0 and r.useable='1' join t_sys_user_role ur on rm.role_id = ur.role_id where menu.parent_id=#{id} and menu.del_flag= 0
and ur.user_id = #{userId}
order by menu.sort /*and (ur.user_id is not null)*/

select distinct menu.*, ur.user_id from  menu
join t_sys_role_menu rm on menu.id = rm.menu_id
join t_sys_role_info r on rm.role_id = r.id and r.del_flag = 0 and r.useable='1'
join t_sys_user_role ur on rm.role_id = ur.role_id
where menu.grade = #{grade} and menu.del_flag = 0

	and menu.parent_id = #{parentID}


	and ur.user_id = #{userId}


	and menu.system_type = #{systemType}

order by menu.sort

mapper.update:
UPDATE  SET
parent_id = #{parentID},
parent_ids = #{parentIds}
WHERE id = #{id}

mapper.select:
select menu.* from  menu
