mapper.select:

select 

from t_wheat_member member
where id = #{id,jdbcType=BIGINT}

select distinct 'true' as QUERYID, , from t_wheat_member member,t_wheat_account account where member.member_id=account.member_id
AND member.name like CONCAT('%', #{name}, '%')
AND member.phone like CONCAT('%', #{phone}, '%')
AND member.member_id like CONCAT('%', #{memberId}, '%')
AND member.level = #{level}
AND member.is_aut = #{isAut}
