mapper.sql:
where 1=1

	and username = #{username}


	and password = #{password}


	and mobile = #{mobile}


	and email = #{email}

mapper.update:
UPDATE
password = #{password}, name = #{name}, email = #{email}, phone = #{phone}, mobile = #{mobile}, user_type = #{userType}, photo = #{photo}, login_ip = #{loginIp}, login_date = #{loginDate}, login_flag = #{loginFlag}, modify_time = now(), remarks = #{remarks}, del_flag = #{delFlag},
WHERE username = #{username}

mapper.select:
SELECT

FROM  a

WHERE a.username = #{username} AND a.del_flag = #{DEL_FLAG_NORMAL}
